import { sendPasswordResetEmail } from "firebase/auth";
import React, { useContext, useState } from "react";
import toast from "react-hot-toast";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { AuthContext } from "../../Context/UserContext/UserContext";
import useTitle from "../../Hooks/useTitle";

const Login = () => {
  useTitle("Login");

  const [error, setError] = useState(null);
  const { signIn, auth, googleSginIn } = useContext(AuthContext);
  const [usermail, setUsermail] = useState("");

  const location = useLocation();
  const from = location.state?.from?.pathname || "/";

  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    const form = e.target;
    const email = form.email.value;
    const password = form.password.value;
    signIn(email, password)
      .then((result) => {
        form.reset();
        toast.success("Logined SuccessFully");
        const user = result.user;
        fetch("https://server-bay-theta.vercel.app/jwt", {
          method: "POST",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify(user),
        })
          .then((res) => res.json())
          .then((data) => {
            localStorage.setItem("RobertToken", data.token);
            toast.success("Logined SuccessFully");
            setTimeout(() => {
              navigate(from, { replace: true });
            }, 500);
          });
      })
      .catch((error) => {
        if (error.message === "Firebase: Error (auth/wrong-password).") {
          setError("Wrong Password");
        } else if (error.message === "Firebase: Error (auth/user-not-found).") {
          setError("User Not Found.Please Register");
        } else {
          setError(error.message);
        }
        console.log("error", error);
      });
  };

  // Google Sign in
  const handleGoogleSignIN = () => {
    googleSginIn()
      .then((result) => {
        console.log(result);
        const user = result.user;
        console.log(user);
        toast.success("Logined SuccessFully");

        fetch("https://server-bay-theta.vercel.app/jwt", {
          method: "POST",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify(user),
        })
          .then((res) => res.json())
          .then((data) => {
            localStorage.setItem("traveloToken", data.token);
            toast.success("Logined SuccessFully");
            setTimeout(() => {
              navigate(from, { replace: true });
            }, 500);
          });
      })
      .catch((error) => {
        console.log("error", error);
      });
  };

  const handleEmailInput = (e) => {
    e.preventDefault();
    const email = e.target.value;

    setUsermail(email);
  };
  const resetPasswrd = () => {
    sendPasswordResetEmail(auth, usermail)
      .then(() => {
        toast.success(
          "Please Check Your Email box to reset your Email Password"
        );
      })
      .catch((error) => {
        setError(error.message);
      });
  };

  return (
    <div className="grid my-10 grid-cols-1 md:grid-cols-2 px-4 md:px-20 mb-16 py-10">
      <img
        className="w-full mx-auto"
        src="https://www.go.ooo/img/bg-img/Login.jpg"
        alt=""
      />
      <div className="w-full max-w-md border border-gray-300 shadow-lg drop-shadow-2xl p-8 space-y-3 rounded-xl bg-white-900 text-gray-900">
        <h1 className="text-2xl text-gray-600 font-bold text-center">Login</h1>
        <form
          onSubmit={handleSubmit}
          noValidate=""
          action=""
          className="space-y-6 ng-untouched ng-pristine ng-valid"
        >
          <div className="space-y-1 text-sm">
            <label
              htmlFor="username"
              className="block font-semibold text-gray-800"
            >
              Email
            </label>
            <input
              type="text"
              onBlur={handleEmailInput}
              name="email"
              id="username"
              placeholder="Email"
              className="w-full px-4 py-3 rounded-md border-gray-700 bg-gray-100 text-gray-800 focus:border-violet-9200"
            />
          </div>
          <div className="space-y-1 text-sm">
            <label
              htmlFor="password"
              className="block font-semibold text-gray-800"
            >
              Password
            </label>
            <input
              type="password"
              name="password"
              id="password"
              placeholder="Password"
              className="w-full px-4 py-3 rounded-md border-gray-700 bg-gray-100 text-gray-800  focus:border-violet-200"
            />
            <div className="flex justify-end text-xs text-gray-400">
              <Link onClick={resetPasswrd} rel="noopener noreferrer" href="/">
                Forgot Password?
              </Link>
            </div>
            <div>
              <p className="text-red-600">{error}</p>
            </div>
          </div>
          <button
            type="submit"
            className="block w-full p-3 text-center rounded-sm font-semibold text-gray-50 bg-gray-600"
          >
            Sign in
          </button>
        </form>
        <div className="flex items-center pt-4 space-x-1">
          <div className="flex-1 h-px sm:w-16 bg-gray-700"></div>
          <p className="px-3 text-sm text-gray-400">
            Login with social accounts
          </p>
          <div className="flex-1 h-px sm:w-16 bg-gray-700"></div>
        </div>
        <div className="flex justify-center space-x-4">
          <button onClick={handleGoogleSignIN} className="p-3 rounded-sm">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 32 32"
              className="w-5 h-5 text-gray-500 fill-current"
            >
              <path d="M16.318 13.714v5.484h9.078c-0.37 2.354-2.745 6.901-9.078 6.901-5.458 0-9.917-4.521-9.917-10.099s4.458-10.099 9.917-10.099c3.109 0 5.193 1.318 6.38 2.464l4.339-4.182c-2.786-2.599-6.396-4.182-10.719-4.182-8.844 0-16 7.151-16 16s7.156 16 16 16c9.234 0 15.365-6.49 15.365-15.635 0-1.052-0.115-1.854-0.255-2.651z"></path>
            </svg>
          </button>
        </div>
        <p className="text-xs text-center sm:px-6 text-gray-400">
          Don't have an account?
          <Link
            rel="noopener noreferrer"
            to="/register"
            className="underline  text-blue-800"
          >
            Sign up
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
